package soejwt

import (
	"log"
	"testing"
)

func TestGetSoeAuthToken(t *testing.T) {
	data, err := GetSoeAuthToken("eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..VrHym-VUAMGAX9vGuQQfzjgslgENSAbXA788WcR_2qk")
	if err != nil {
		t.Fatal(err)
	}

	log.Println(data)
}

func TestParseToken(t *testing.T) {
	data, err := ParseToken("Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..VrHym-VUAMGAX9vGuQQfzjgslgENSAbXA788WcR_2qk")
	if err != nil {
		t.Fatal(err)
	}

	log.Println(data)
}
